// Load in begin.
jQuery( document ).ready(function() {
    jQuery("body").addClass("body").addClass("wrap");

    var $wrap = jQuery("#wrap");
    $wrap.prepend("<div id='wrap_content'></div>");

    var $wrap_content = jQuery("#wrap_content");
    $wrap_content.append( jQuery("#sw_c1") );
    $wrap_content.append( jQuery("#sw_c3") );
    $wrap_content.append( jQuery("#sw_c2") );
    $wrap_content.append( jQuery("#sw_f") );
    $wrap.prepend( jQuery("#toplogo") );

    $wrap.addClass("wrap");

    jQuery("#sw_c1").addClass("sw_top");
    jQuery("#sw_c3").addClass("sw_top");

    jQuery("a[rel*='shadowbox'] img").addClass("shadowbox_add");


    // Hide advertisement.
    jQuery(".slza").hide();
    jQuery("#swz1, #swz2").hide();
    jQuery("#sw_f div").first().hide();
});


// Beep for chyatik.
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

// Correct lolresponse() function.
function lolresponse(response) {
        (window.dlay);
        var AmIPeriod = (this == window.lol);

        if (response && (!AmIPeriod || !window.immasendin)) {
                if (response == 'stop') {
                        alert ('Чатик больше не обновляется, он устал.');
                        return false;
                }

                window.dlayamount = window.min_dlayamount;

                var m = response.match(/wohoo(\d+) (\d+)/);
                var new_start = m[1];
                var new_end = m[2];

                if (window.lastid < new_end) {
                        var newdiv = new Element('div', {
                                styles: {
                                        opacity: 0
                                }
                        }).set('html', response);

                        ScrollaOrNot();

                        beep();                 // ADD THIS

                        newdiv.inject(document.id('chats'));
                        newdiv.tween('opacity', 1);

                        var list = newdiv.getElements('a');
                  list = list.filter(function(item){
                        //if (item.hasClass('no')) return false;
                    return item.href.test(/\.(jpe?g|png|gif|swf)$/)
                  });
                  if (list && list.length) {
                                Shadowbox.setup(list, Shadowbox_options);
                  }

                        window.lastid = new_end;
                }
        } else {
                if (window.dlayamount < window.max_dlayamount) window.dlayamount *= window.delta_dlayamount;
        }
        if (AmIPeriod) {
                another();
        } else {
                setTimeout('window.immasendin = false;', window.dlayamount * window.delta_dlayamount + 50);
        }
}
